import React, { useState } from 'react';

const API_KEY = "H26hdxkNLCsDWSf0va7zkKgWhbEEv_vnJxnfk-OOLhI";

const HomePage = props => {

    const [images, setImages] = useState([])
    const [value, setValue] = useState("");
    const url = `https://api.unsplash.com/search/photos/?&query=${value}&client_id=${API_KEY}`
    // const getInput = (e) => {
    //     console.log(e.target.value)
    //     setValue(e.target.value)
    // }
    const onSearch = () =>{
        fetch(url)
            .then(response => response.json())
            .then(data => {
                // console.log(data.results[1].urls.regular)
                setImages(data.results);
                console.log("clicked")
            })
    }
    return (
        <div>
            {/* <input
                className="inputStyle"
                type="text"
                placeholder="Search for images here..."
                onChange={getInput}
            />
            <button onClick={onSearch}>Search</button> */}
            <SearchBox showImage={onSearch} />
            {images.map((item)=>{
                return  <img src={item.urls.regular} />})}           
        </div>
    )
}

export default HomePage;
