import React, { useState } from 'react';
import SearchBox from '../components/searchBox'
import Image from '../components/image'

const API_KEY = "H26hdxkNLCsDWSf0va7zkKgWhbEEv_vnJxnfk-OOLhI";

const HomePage = props => {

    const [images, setImages] = useState([])
    const [value, setValue] = useState("");
    const url = `https://api.unsplash.com/search/photos/?&query=${value}&client_id=${API_KEY}`
    const onSearchSubmit = () => {
        setValue
        fetch(url)
            .then(response => response.json())
            .then(data => {
                console.log(data)
                setImages(data.results);
            })
    }

    return (
        <div>
            <input
                className="inputStyle"
                type="text"
                placeholder="Search for images here..."
                value={value}
                onChange={onSearchSubmit}
            />
            {/* <SearchBox userSubmit={onSearchSubmit}/> */}
            {/* <Image foundImages={setImages} /> */}
        </div>
    )
}

export default HomePage;
