import React, { useState, useEffect } from 'react';
import './search.css'

const API_KEY = "H26hdxkNLCsDWSf0va7zkKgWhbEEv_vnJxnfk-OOLhI";

const Search = props => {

    const [images, setImages] = useState([]);
    const [value, setValue] = useState("");
    const url = `https://api.unsplash.com/search/photos/?&query=${value}&client_id=${API_KEY}`;

    const getInput = (e) => {
        console.log(e.target.value);
        setValue(e.target.value);
    }

    const onSearch = () => {
        fetch(url)
            .then(response => response.json())
            .then(data => {
                setImages(data.results);
            })
    }

    const handleClick = (value) => {
        if (value === "license_document") {
            setShowModal(true);
            setDisp_img(props.vehicleData.license_document);
            setName_img("license_document");
            setAlt_img("license_document");
        } else if (value === "v5_document") {
            setShowModal(true);
            setDisp_img(props.vehicleData.v5_document);
            setName_img("v5_document");
            setAlt_img("v5_document");
        } else if (value === "insurance_document") {
            setShowModal(true);
            setDisp_img(props.vehicleData.insurance_document);
            setName_img("insurance_document");
            setAlt_img("insurance_document");
        } else if (value === "image") {
            setShowModal(true);
            setDisp_img(props.vehicleData.image);
            setName_img("image");
            setAlt_img("image");
        }
    };

    const ClostModal = () => {
        setShowModal(false);
    }

    return (
        <div>
            <div className="searchStyle">
            <input
                className="inputSearch"
                type="text"
                placeholder="Search for images here..."
                onChange={getInput}
            />
            <button onClick={onSearch}>Search</button>
            </div>
            <div className="imagePageShow">
                {images.map((item) => {
                    return <img className="imageShow" onClick={
                        handleClick} key={item.id} src={item.urls.regular} />
                })}
            </div>
        </div>
    )
}
export default Search;
