import React, { useState, useEffect } from 'react';
import './search.css'
import Modal from 'react-modal';
const API_KEY = "H26hdxkNLCsDWSf0va7zkKgWhbEEv_vnJxnfk-OOLhI";
// import { faEnvelope } from '@fortawesome/free-solid-svg-icons';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const Search = props => {

    const [images, setImages] = useState([]);
    const [value, setValue] = useState("");
    const [currentImage, setcurrentImage] = useState(false)
    const url = `https://api.unsplash.com/search/photos/?&query=${value}&per_page=9&client_id=${API_KEY}`;
    const [modalIsOpen, setIsOpen] = React.useState(false);

    // const customStyles = {
    //     content: {
    //         top: '50%',
    //         left: '50%',
    //         right: 'auto',
    //         bottom: 'auto',
    //         marginRight: '-50%',
    //         transform: 'translate(-50%, -50%)'
    //     }
    // };

    const openModal = (event) => {
        console.log(event.target.src);
        setcurrentImage(event.target.src);
        setIsOpen(true);
        
    }

    const closeModal = () => {
        setcurrentImage(false);
        setIsOpen(false);
    }

    const getInput = (e) => {
        console.log(e.target.value);
        setValue(e.target.value);
    }

    const onSearch = () => {
        fetch(url)
            .then(response => response.json())
            .then(data => {
                setImages(data.results);
            })
    }
    console.log(images)
    return (
        <div>
            <div className="searchStyle">
                <input
                    className="inputSearch"
                    type="text"
                    placeholder="Search for images here..."
                    onChange={getInput}
                />
                <button onClick={onSearch}>Search</button>
                {/* <FontAwesomeIcon className="notifyFont" icon={faEnvelope}/> */}
            </div>
            <div className="imagePageShow">
                {images.map((item) => {
                    return <img className="imageShow" onClick={(event) => openModal(event)}  key={item.id} src={item.urls.regular} />
                })}
            </div>
            <Modal
                isOpen={modalIsOpen}
                onRequestClose={closeModal}
                className="modalStyle"
                // style={customStyles}
            >
                <div className="modalStyle">
                <img className="imageShow" src={currentImage} />
                </div>
            </Modal>
        </div>
    )
}
export default Search;
