import React, { useState, useEffect } from 'react';
import './search.css'

const API_KEY = "H26hdxkNLCsDWSf0va7zkKgWhbEEv_vnJxnfk-OOLhI";

const Search = props => {

    const [images, setImages] = useState([]);
    const [value, setValue] = useState("");
    const url = `https://api.unsplash.com/search/photos/?&query=${value}&client_id=${API_KEY}`;

    const getInput = (e) => {
        console.log(e.target.value);
        setValue(e.target.value);
    }

    const onSearch = () => {
        fetch(url)
            .then(response => response.json())
            .then(data => {
                setImages(data.results);
            })
    }

    const [width, setWidth] = React.useState(window.innerWidth);
    useEffect(() => {
        window.addEventListener('resize', handleWindowSizeChange);
    });
    const handleWindowSizeChange = () => {
        setWidth(window.innerWidth);
    };

    return (
        <div>
            <div className="searchStyle">
            <input
                className="inputSearch"
                type="text"
                placeholder="Search for images here..."
                onChange={getInput}
            />
            <button onClick={onSearch}>Search</button>
            </div>
            <div className="imagePageShow">
                {images.map((item) => {
                    return <img className="imageShow" key={item.id} src={item.urls.regular} />
                })}
            </div>
        </div>
    )
}
export default Search;
