import React, { useState, useEffect } from 'react';
import './search.css'
import Modal from 'react-bootstrap/Modal';

const API_KEY = "H26hdxkNLCsDWSf0va7zkKgWhbEEv_vnJxnfk-OOLhI";

const Search = props => {

    const [images, setImages] = useState([]);
    const [value, setValue] = useState("");
    const url = `https://api.unsplash.com/search/photos/?&query=${value}&client_id=${API_KEY}`;
    // const [showModal, setShowModal] = useState(false);
    const getInput = (e) => {
        console.log(e.target.value);
        setValue(e.target.value);
    }

    const onSearch = () => {
        fetch(url)
            .then(response => response.json())
            .then(data => {
                setImages(data.results);
            })
    }

    // const handleClick = (e) => {
    //     setShowModal(true);
    // };

    // const ClostModal = () => {
    //     setShowModal(false);
    // }

    return (
        <div>
            <div className="searchStyle">
                <input
                    className="inputSearch"
                    type="text"
                    placeholder="Search for images here..."
                    onChange={getInput}
                />
                <button onClick={onSearch}>Search</button>
            </div>
            <div className="imagePageShow">
                {images.map((item) => {
                    return <img className="imageShow" 
                    // onClick={handleClick} 
                    key={item.id} src={item.urls.regular} />
                })}
            </div>
            {/* {
                showModal && <Modal
                    size="xl"
                    show={showModal}
                    aria-labelledby="ModalHeader"
                >
                    <div className="modal-header">
                        <h5 className="modal-title" id="exampleModalLabel">Vehicle Details</h5>
                        <a className='btn btn-default' href onClick={ClostModal}>X</a>
                    </div>
                    <img className="imageShow" key={item.id} src={item.urls.regular} />
                </Modal>} */}
        </div>
    )
}
export default Search;
