import React, { useState, useEffect } from 'react';
import './search.css'
import Modal from 'react-modal';
const API_KEY = "H26hdxkNLCsDWSf0va7zkKgWhbEEv_vnJxnfk-OOLhI";

const Search = props => {

    const [images, setImages] = useState([]);
    const [value, setValue] = useState("");
    const [currentImage, setcurrentImage] = useState(false)
    const url = `https://api.unsplash.com/search/photos/?&query=${value}&per_page=9&client_id=${API_KEY}`;

    const [modalIsOpen, setIsOpen] = React.useState(false);

    const customStyles = {
        content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
            background: 'black',
            borderRadius: '2rem',
        }
    };

    const openModal = (event) => {
        console.log(event.target.src);
        setcurrentImage(event.target.src);
        setIsOpen(true);        
    }

    const closeModal = () => {
        setcurrentImage(false);
        setIsOpen(false);
    }

    const getInput = (e) => {
        console.log(e.target.value);
        setValue(e.target.value);
    }

    const onSearch = () => {
        fetch(url)
            .then(response => response.json())
            .then(data => {
                setImages(data.results);
            })
    }
    console.log(images)
    return (
        <div>
            <div className="searchStyle">
                <input
                    className="inputSearch"
                    type="text"
                    placeholder="Search for images here..."
                    onChange={getInput}
                />
                {/* <img className="searchIcon" 
                src={search}
                /> */}
                <button onClick={onSearch}>Search</button>
            </div>
            <div className="imagePageShow">
                {images.map((item) => {
                    return(
                    <div className="container">
                    <img className="imageShow" onClick={(event) => openModal(event)}  key={item.id} src={item.urls.regular} /> 
                    <div></div> 
                    <div className="bottom-left">{item.user.username}</div> 
                    </div>)                
                })
                }
                
            </div>
            
            <Modal
                isOpen={modalIsOpen}
                onRequestClose={closeModal}
                style={customStyles}
            >
                <a href onClick={closeModal}>X</a>
                <img className="modalimage" src={currentImage} />
                <div className="downloadButton">
                <button className="downloadButton">Download</button>
                </div>
            </Modal>
        </div>
    )
}
export default Search;
