import React from 'react';
import SearchBox from '../components/searchBox'
import Image from '../components/image'

const API_KEY = "H26hdxkNLCsDWSf0va7zkKgWhbEEv_vnJxnfk-OOLhI";

const HomePage = props => {
    const [state, setState] = { images: [] };

    const onSearchSubmit = async (term) => {
        const response = await fetch('https://api.unsplash.com/search/photos?query= description',
        {
            method:'GET',
            params: { query: term},
            headers: {
                Authorization: `Client-ID ${API_KEY}`
            }
        }).then(response => response.json())
        .then(data => 
            // console.log(data)
        setState({ images: response.data.results })
        );

        // this.setState({ images: response.data.results })
        // console.log(response);
    }
        return (
            <div>
                <SearchBox userSubmit={onSearchSubmit}/>
                <Image foundImages={state.images} />
            </div>
        )
    }

export default HomePage;
