import React from 'react';
import SearchBox from '../components/searchBox'
import Image from '../components/image'

const API_KEY = process.env.REACT_APP_API_KEY;

const HomePage = props => {
    const state = { images: [] };
console.log('https://api.unsplash.com/search/photos')
    const onSearchSubmit = async (term) => {
        const response = await fetch('https://api.unsplash.com/search/photos',
        {
            params: { query: term},
            headers: {
                Authorization: `Client-ID ${API_KEY}`
            }
        })

        this.setState({ images: response.data.results })
        console.log(response);
    }
        return (
            <div>
                <SearchBox userSubmit={onSearchSubmit}/>
                {/* <span>Found: {state.images.length} images</span> */}
                <Image foundImages={state.images} />
            </div>
        )
    }

export default HomePage;
